mapper.select:
SELECT oh.orderId orderId, oh.orderCode orderCode, oh.inserted inserted, oh.writer writer, oh.message message, oh.comment comment, oh.deliveryDate deliveryDate
FROM Order_header oh JOIN Buyer b ON oh.buyerCode = b.buyerCode
WHERE oh.orderId = #{orderId};

mapper.update:
UPDATE Order_header
SET status = "승인완료",
	comment = #{comment}
WHERE orderId = #{orderId};

UPDATE Order_header
SET status = "요청반려",
	comment = #{comment}
WHERE orderId = #{orderId};

UPDATE Order_header
SET status = "거래종결"
WHERE orderId = #{orderId};
